import React, { useState, useEffect } from "react";
import Board from "../components/Board";
import Configuration from "../components/Configuration";
import { api } from "../utils/api";
import { Box, Typography, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import {BoardSizeConfiguration} from "../components/Configuration"

const boardSizeConfiguration: BoardSizeConfiguration = {
  id:`000000000000-0000-0000-0000-000000000000`,
  label: "3 x 3",
  value:"3"
}

const boardSizeConfigurations: BoardSizeConfiguration[] = [{
  id:`000000000000-0000-0000-0000-000000000000`,
  label: "3 x 3",
  value:"3"
},
{
  id:`000000000000-0000-0000-0000-000000000000`,
  label: "4 x 4",
  value:"4"
}]

const Game: React.FC = () => {
  const [boardSize, setBoardSize] = useState(4);
  const [gameMode, setGameMode] = useState<string>("ai");
  const [aiDifficulty, setAIDifficulty] = useState<string>("hard");
  const [boardSizeOptions, setBoardSizeOptions] = useState<BoardSizeConfiguration[]>(boardSizeConfigurations);
  const [isTimerEnabled, setIsTimerEnabled] = useState(false);
  const [timerDuration, setTimerDuration] = useState(30);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const resetBoard = () => {
    // If game is over or board is empty, reset the board
    navigate("/gameboard");
  };
  // eslint-disable-next-line @typescript-eslint/no-unused-vars

  const token = sessionStorage.getItem('token'); // Retrieve token from storage

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8080/tictactoe_game_service/client/configuration', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`, // Include the authorization header
            'Content-Type': 'application/json'
          },
        });

        console.log("Login successful:", response.ok);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const json = await response.json();
        setBoardSize(json);
        setAIDifficulty(json);
        setGameMode(json);
      } catch (error) {
        console.error("Fetching error:", error);
        // Handle the error appropriately, e.g., display an error message
      }
    };

    if (token) {
      fetchData();
    } else {
      // Handle the case where there is no token, e.g., redirect to login
      console.warn('No token available, cannot fetch data.');
    }
  }, [token]); // Effect runs when token changes


  return (
    <Box sx={{ textAlign: "center", py: 5 }}>
      <Typography
        variant="h4"
        gutterBottom
        sx={{ fontFamily: "Poppins", mb: 2, fontWeight: "bold" }}
      >
        TicTacToe Game
      </Typography>
      {/* Divider */}
      <div
        style={{
          width: "100%",
          borderBottom: "1px solid #e0e0e0",
          margin: "0 auto 20px",
        }}
      />
      <Typography
        variant="h6"
        gutterBottom
        sx={{ fontFamily: "Poppins", mb: 0 }}
      >
        Configuration
      </Typography>
      <Configuration
        boardSize={boardSize}
        setBoardSize={setBoardSize}
        gameMode={gameMode}
        setGameMode={setGameMode}
        aiDifficulty={aiDifficulty}
        setAIDifficulty={setAIDifficulty}
        boardSizeOptions={boardSizeOptions}
        setBoardSizeOptions={setBoardSizeOptions}
        isTimerEnabled={isTimerEnabled}
        setIsTimerEnabled={setIsTimerEnabled}
        timerDuration={timerDuration}
        setTimerDuration={setTimerDuration}
      />
      {/* Divider */}
      <div
        style={{
          width: "100%",
          borderBottom: "1px solid #e0e0e0",
          margin: "0 auto 20px",
        }}
      />
      <Button
            variant="contained"
            color="primary"
            type="submit"
            fullWidth
            disabled={loading}
            onClick={resetBoard}
            sx={{
              mt: 2,
              fontFamily: "Poppins",
              fontWeight: "bold",
              py: 1.5,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            "Play Game"
          </Button>

      {/* <Board
        boardSize={boardSize}
        isAI={gameMode === "ai"}
        aiDifficulty={aiDifficulty}
        isTimerEnabled={isTimerEnabled}
        timerDuration={timerDuration}
        gameMode={gameMode}
      /> */}
    </Box>
  );
};

export default Game;
