import React from "react";
import {
  Box,
  Typography,
  Button,
  Container,
  Grid,
  Card,
  CardContent,
  CardActions,
} from "@mui/material";
import { Link } from "react-router-dom";
import { useTheme } from "@mui/material/styles";
import { useMediaQuery } from "@mui/material";
import { keyframes } from "@emotion/react";

const slideUp = keyframes`
  from {
    transform: translateY(30px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
`;

const LandingPage: React.FC = () => {
  const theme = useTheme();
  const isDarkMode = theme.palette.mode === "dark";
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

  const themeColor = "#1976d2";

  return (
    <Box
      sx={{
        minHeight: "100vh",
        backgroundColor: isDarkMode ? "#1e1e1e" : "#f5f5f5",
        padding: "2rem 0",
        transition: "background-color 0.3s ease",
      }}
    >

      {/* Additional Information Section */}
      <Box
        sx={{
          mt: 6,
          py: 4,
          textAlign: "center",
          backgroundColor: themeColor,
          color: "white",
          borderRadius: 2,
          boxShadow: 3,
          animation: `${slideUp} 0.8s ease-out`,
        }}
      >
        <Typography
          variant="h5"
          sx={{
            mb: 2,
            fontFamily: "Poppins",
            fontWeight: 600,
          }}
        >
          Welcome to Tic Tac Toe Game
        </Typography>
        <Typography
          variant="body1"
          sx={{
            maxWidth: 800,
            mx: "auto",
            fontFamily: "Poppins",
          }}
        >
          Experience the classic game with AI opponents, global leaderboards,
          and customizable gameplay!
        </Typography>
      </Box>

      {/* Additional Information Section */}
      <Box
        sx={{
          mt: 6,
          py: 4,
          textAlign: "center",
          backgroundColor: isDarkMode ? "#333" : "#f5f5f5",
          color: isDarkMode ? "white" : "black",
          borderRadius: 2,
          boxShadow: 3,
          animation: `${slideUp} 1s ease-out`,
        }}
      >
        <Typography
          variant="h5"
          sx={{
            mb: 2,
            fontFamily: "Poppins",
            fontWeight: 600,
          }}
        >
          Ready to Get Started?
        </Typography>
        <Typography
          variant="body1"
          sx={{
            maxWidth: 800,
            mx: "auto",
            fontFamily: "Poppins",
          }}
        >
          Join Tic Tac Toe Pro today and experience the ultimate gaming
          platform. Play against AI, challenge your friends, and climb the
          global leaderboard!
        </Typography>
        <Button
          component={Link}
          to="/game"
          variant="contained"
          sx={{
            mt: 3,
            backgroundColor: themeColor,
            color: "white",
            padding: "0.75rem 1.5rem",
            margin: 3,
            fontFamily: "Poppins",
            fontWeight: 600,
            "&:hover": {
              backgroundColor: "#005bb5",
            },
          }}
        >
         To Game
        </Button>
        <Button
          component={Link}
          to="/signup"
          variant="contained"
          sx={{
            mt: 3,
            backgroundColor: themeColor,
            color: "white",
            padding: "0.75rem 1.5rem",
            margin: 3,
            fontFamily: "Poppins",
            fontWeight: 600,
            "&:hover": {
              backgroundColor: "#005bb5",
            },
          }}
        >
          Signup
        </Button>
        <Typography variant="body1" sx={{ mt: 2, fontFamily: "Poppins" }}>
          Already have an account?{" "}
          <Link to="/login" style={{ color: themeColor }}>
            Login
          </Link>
        </Typography>
      </Box>
    </Box>
  );
};

export default LandingPage;
