import React, { useState, useEffect } from "react";
import Board from "../components/Board";
import Configuration from "../components/Configuration";
import { api } from "../utils/api";
import { Box, Typography, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";

const Game: React.FC = () => {
  const [boardSize, setBoardSize] = useState(4);
  const [gameMode, setGameMode] = useState<"ai" | "local" | "online">("ai");
  const [aiDifficulty, setAIDifficulty] = useState<
    "easy" | "medium" | "hard" | "impossible"
  >("medium");
  const [isTimerEnabled, setIsTimerEnabled] = useState(false);
  const [timerDuration, setTimerDuration] = useState(30);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const resetBoard = () => {
    // If game is over or board is empty, reset the board
    navigate("/gameboard");
  };
  // eslint-disable-next-line @typescript-eslint/no-unused-vars

  // Fetch leaderboard data
    // useEffect(() => {
    //   api.defaults.headers["Authorization"] = `Bearer ${sessionStorage.getItem("token")}`;
    //   api
    //     .get("/client/configuration")
    //     .then((response) => {
    //       setBoardSize(response.data);
    //       setAIDifficulty(response.data);
    //       setGameMode(response.data);
    //     })
    //     .catch((error) => console.error("Failed to fetch configuration:", error));
    // }, []);

  return (
    <Box sx={{ textAlign: "center", py: 5 }}>
      <Typography
        variant="h4"
        gutterBottom
        sx={{ fontFamily: "Poppins", mb: 2, fontWeight: "bold" }}
      >
        TicTacToe Game
      </Typography>
      {/* Divider */}
      <div
        style={{
          width: "100%",
          borderBottom: "1px solid #e0e0e0",
          margin: "0 auto 20px",
        }}
      />
      <Typography
        variant="h6"
        gutterBottom
        sx={{ fontFamily: "Poppins", mb: 0 }}
      >
        Configuration
      </Typography>
      <Configuration
        boardSize={boardSize}
        setBoardSize={setBoardSize}
        gameMode={gameMode}
        setGameMode={setGameMode}
        aiDifficulty={aiDifficulty}
        setAIDifficulty={setAIDifficulty}
        isTimerEnabled={isTimerEnabled}
        setIsTimerEnabled={setIsTimerEnabled}
        timerDuration={timerDuration}
        setTimerDuration={setTimerDuration}
      />
      {/* Divider */}
      <div
        style={{
          width: "100%",
          borderBottom: "1px solid #e0e0e0",
          margin: "0 auto 20px",
        }}
      />
      <Button
            variant="contained"
            color="primary"
            type="submit"
            fullWidth
            disabled={loading}
            onClick={resetBoard}
            sx={{
              mt: 2,
              fontFamily: "Poppins",
              fontWeight: "bold",
              py: 1.5,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            "Play Game"
          </Button>

      {/* <Board
        boardSize={boardSize}
        isAI={gameMode === "ai"}
        aiDifficulty={aiDifficulty}
        isTimerEnabled={isTimerEnabled}
        timerDuration={timerDuration}
        gameMode={gameMode}
      /> */}
    </Box>
  );
};

export default Game;
