import React, { useState, useEffect, useRef } from "react";
import { api } from "../utils/api";
import {
  Typography,
  Box,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Paper,
  CircularProgress,
  TextField,
  InputAdornment,
  Table
} from "@mui/material";
import {
  ArrowUpward,
  ArrowDownward,
  Remove,
  Search,
} from "@mui/icons-material";

const Leaderboard: React.FC = () => {
  const [leaderboard, setLeaderboard] = useState([]);
  const [filteredLeaderboard, setFilteredLeaderboard] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchLoading, setSearchLoading] = useState(false);
  const [search, setSearch] = useState("");

  const debounceTimeout = useRef<number | null>(null);

  // Fetch leaderboard data
  useEffect(() => {
    api
      .get("/leaderboard")
      .then((response) => {
        setLeaderboard(response.data);
        setFilteredLeaderboard(response.data);
      })
      .catch((error) => console.error("Failed to fetch leaderboard:", error))
      .finally(() => setLoading(false));
  }, []);

  // Custom debounce function
  const debounce = (callback: Function, delay: number) => {
    return (...args: any) => {
      if (debounceTimeout.current) {
        clearTimeout(debounceTimeout.current);
      }
      debounceTimeout.current = window.setTimeout(() => {
        callback(...args);
      }, delay);
    };
  };

  const handleSearch = (value: string) => {
    setSearchLoading(true);
    const filtered = leaderboard.filter((entry: any) =>
      entry.username.toLowerCase().includes(value.toLowerCase()),
    );
    setFilteredLeaderboard(filtered);
    setSearchLoading(false);
  };

  const debouncedSearch = debounce(handleSearch, 300);

  const onSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setSearch(value);
    debouncedSearch(value);
  };

  const calculateWinRate = (wins: number, losses: number, draws: number) => {
    const totalMatches = wins + losses + draws;
    return totalMatches > 0 ? ((wins / totalMatches) * 100).toFixed(2) : "0.00";
  };

  const getWinRateColor = (winRate: number) => {
    if (winRate > 60) return "green";
    if (winRate >= 40) return "orange";
    return "red";
  };

  if (loading) {
    return (
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          minHeight: "100vh",
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box
      sx={{
        p: 4,
        maxWidth: 700,
        mx: "auto",
        textAlign: "center",
        fontFamily: "Poppins, sans-serif",
      }}
    >
      <Typography
        variant="h4"
        gutterBottom
        sx={{ fontFamily: "Poppins, sans-serif", mb: 3, fontWeight: "bold" }}
      >
        Configuration
      </Typography>
      <Table></Table>
    </Box>
  );
};

export default Leaderboard;
