import React, { useState } from "react";
import {
  Box,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  TextField,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  Typography,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  Button,
  useMediaQuery,
  Tooltip,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  Switch,
} from "@mui/material";
import { SelectChangeEvent } from "@mui/material";
import { useTheme } from "@mui/system";
import { UUID } from "crypto";

export interface GameMode {
  id: UUID;
  mode: string;
  label: string;
}

export interface AIDifficulty {
  id: UUID;
  value: string;
  label: string;
}

export interface BoardSizeConfiguration {
  id: UUID;
  value: string;
  label: string;
}

export interface TimeLeft {
  id: UUID;
  value: number;
  label: string;
}

interface ConfigurationProps {
  boardSize: number;
  setBoardSize: (size: number) => void;
  gameMode: string;
  setGameMode: (mode:string) => void;
  aiDifficulty: string;
  setAIDifficulty: (
    difficulty: string,
  ) => void;
  boardSizeOptions:BoardSizeConfiguration[];
  setBoardSizeOptions:(configuration:BoardSizeConfiguration[]) => void;
  gameModeOptions:GameMode[];
  setGameModeOptions:(configuration:GameMode[]) => void;
  aiDifficultyOptions:AIDifficulty[];
  setAIDifficultyOptions:(configuration:AIDifficulty[]) => void;
  timeLeft: number;
  setTimeLeft: (size: number) => void;
  timeLeftOptions:TimeLeft[];
  setTimeLeftOptions:(configuration:TimeLeft[]) => void;
  isTimerEnabled: boolean;
  setIsTimerEnabled: (enabled: boolean) => void;
  // timerDuration: number;
  // setTimerDuration: (duration: number) => void;
}

const Configuration: React.FC<ConfigurationProps> = ({
  boardSize,
  setBoardSize,
  gameMode,
  setGameMode,
  aiDifficulty,
  setAIDifficulty,
  boardSizeOptions,
  setBoardSizeOptions,
  gameModeOptions,
  setGameModeOptions,
  aiDifficultyOptions,
  setAIDifficultyOptions,
  timeLeft,
  setTimeLeft,
  timeLeftOptions,
  setTimeLeftOptions,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  isTimerEnabled,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  setIsTimerEnabled,
  // timerDuration,
  // setTimerDuration,
}) => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const [inputTimer, setInputTimer] = useState(timeLeft);

  const handleBoardSizeChange = (event: SelectChangeEvent<number>) => {
    const newSize = parseInt(event.target.value as unknown as string, 10);
    if (newSize >= 3) {
      setBoardSize(newSize);
      console.log(event.target.value);
    }
  };

  const handleTimeLeftChange = (event: SelectChangeEvent<number>) => {
    const newSize = parseInt(event.target.value as unknown as string, 10);
    setTimeLeft(newSize);
    console.log(event.target.value);
  };

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  // const handleSetTimer = () => {
  //   if (inputTimer >= 10 && inputTimer <= 300) {
  //     setTimerDuration(inputTimer);
  //   }
  // };

  const handleGameModeChange = (
    event: SelectChangeEvent<string>,
  ) => {
    setGameMode(event.target.value);
  };

  // const boardSizeOptions = [
  //   { value: 3, label: "3 x 3" },
  //   { value: 4, label: "4 x 4 (Recommended)" },
  //   { value: 5, label: "5 x 5" },
  //   { value: 6, label: "6 x 6" },
  //   { value: 7, label: "7 x 7" },
  //   { value: 8, label: "8 x 8" },
  //   { value: 9, label: "9 x 9" },
  //   { value: 10, label: "10 x 10" },
  // ];

  const isOnlineMode = gameMode === "online";

  return (
    <Box
      sx={{
        textAlign: "center",
        mb: 0,
        p: isSmallScreen ? 2 : 4,
        display: "flex",
        flexDirection: "column",
        gap: 3,
        alignItems: "center",
      }}
    >
      {/* Game Mode Settings */}
      <FormControl sx={{ minWidth: 200 }}>
        <InputLabel id="game-mode-label" sx={{ fontFamily: "Poppins" }}>
          Game Mode
        </InputLabel>
        <Select
          labelId="game-mode-label"
          value={gameMode}
          onChange={handleGameModeChange}
          label="Game Mode"
          variant="outlined"
          sx={{
            fontFamily: "Poppins",
            ".MuiOutlinedInput-notchedOutline": {
              borderColor:
                theme.palette.mode === "dark" ? "#ffffff" : "#000000",
            },
            "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
              borderColor:
                theme.palette.mode === "dark" ? "#ffffff" : "#000000",
            },
            ".MuiSvgIcon-root": {
              color: theme.palette.mode === "dark" ? "#ffffff" : "#000000",
            },
          }}
        >
          {gameModeOptions.map((option) => (
              <MenuItem
                key={option.mode}
                value={option.mode}
                sx={{ fontFamily: "Poppins" }}
              >
                {option.label}
              </MenuItem>
            ))}
        </Select>
      </FormControl>

      {/* Board Size Settings */}
      <Tooltip
        title={
          isOnlineMode
            ? "Board size is fixed at 4x4 in online mode.":""
        }
      >
        <FormControl sx={{ minWidth: 200 }} disabled={isOnlineMode}>
          <InputLabel id="board-size-label" sx={{ fontFamily: "Poppins" }}>
            Board Size
          </InputLabel>
          <Select
            labelId="board-size-label"
            value={boardSize}
            onChange={handleBoardSizeChange}
            label="Board Size"
            variant="outlined"
            sx={{
              fontFamily: "Poppins",
              ".MuiOutlinedInput-notchedOutline": {
                borderColor:
                  theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
              "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                borderColor:
                  theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
              ".MuiSvgIcon-root": {
                color: theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
            }}
          >
            {boardSizeOptions.map((option) => (
              <MenuItem
                key={option.value}
                value={option.value}
                sx={{ fontFamily: "Poppins" }}
              >
                {option.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </Tooltip>

      {/* AI Difficulty Settings */}
      {gameMode === "ai" && (
        <FormControl sx={{ minWidth: 200 }}>
          <InputLabel
            id="ai-difficulty-label"
            sx={{
              fontFamily: "Poppins",
            }}
          >
            AI Difficulty
          </InputLabel>
          <Select
            labelId="ai-difficulty-label"
            value={aiDifficulty}
            onChange={(e) => setAIDifficulty(e.target.value as any)}
            label="AI Difficulty"
            variant="outlined"
            sx={{
              fontFamily: "Poppins",
              ".MuiOutlinedInput-notchedOutline": {
                borderColor:
                  theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
              "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                borderColor:
                  theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
              ".MuiSvgIcon-root": {
                color: theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
            }}
          >
             {aiDifficultyOptions.map((option) => (
              <MenuItem
                key={option.value}
                value={option.value}
                sx={{ fontFamily: "Poppins" }}
              >
                {option.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      )}

        <FormControl sx={{ minWidth: 200 }}>
          <InputLabel
            id="time_left_label"
            sx={{
              fontFamily: "Poppins",
            }}
          >
            Time Duration
          </InputLabel>
          <Select
            labelId="time_left_label"
            value={timeLeft}
            onChange={handleTimeLeftChange}
            label="Time Duration"
            variant="outlined"
            sx={{
              fontFamily: "Poppins",
              ".MuiOutlinedInput-notchedOutline": {
                borderColor:
                  theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
              "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
                borderColor:
                  theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
              ".MuiSvgIcon-root": {
                color: theme.palette.mode === "dark" ? "#ffffff" : "#000000",
              },
            }}
          >
             {timeLeftOptions.map((option) => (
              <MenuItem
                key={option.value}
                value={option.value}
                sx={{ fontFamily: "Poppins" }}
              >
                {option.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

      {/* Timer Settings */}
      <Box
        sx={{
          display: "flex",
          flexDirection: isSmallScreen ? "column" : "row",
          alignItems: "center",
          gap: 2,
        }}
      >
        {/*<Tooltip*/}
        {/*  title={*/}
        {/*    isOnlineMode*/}
        {/*      ? "Timer is disabled in online mode."*/}
        {/*      : "Enable or disable the timer for the game."*/}
        {/*  }*/}
        {/*>*/}
        {/*  <Box*/}
        {/*    sx={{*/}
        {/*      display: "flex",*/}
        {/*      alignItems: "center",*/}
        {/*      gap: 1,*/}
        {/*      opacity: isOnlineMode ? 0.5 : 1,*/}
        {/*    }}*/}
        {/*  >*/}
        {/*    <Typography variant="body1" sx={{ fontFamily: "Poppins" }}>*/}
        {/*      Enable Timer*/}
        {/*    </Typography>*/}
        {/*    <Switch*/}
        {/*      checked={isTimerEnabled}*/}
        {/*      onChange={(e) => setIsTimerEnabled(e.target.checked)}*/}
        {/*      disabled={isOnlineMode}*/}
        {/*    />*/}
        {/*  </Box>*/}
        {/*</Tooltip>*/}
        {/*{isTimerEnabled && !isOnlineMode && (*/}
        {/*  <Box*/}
        {/*    sx={{*/}
        {/*      display: "flex",*/}
        {/*      flexDirection: isSmallScreen ? "column" : "row",*/}
        {/*      gap: 2,*/}
        {/*      alignItems: "center",*/}
        {/*    }}*/}
        {/*  >*/}
        {/*    <TextField*/}
        {/*      label="Timer (seconds)"*/}
        {/*      type="number"*/}
        {/*      value={inputTimer}*/}
        {/*      onChange={(e) => setInputTimer(Number(e.target.value))}*/}
        {/*      inputProps={{*/}
        {/*        min: 10,*/}
        {/*        max: 300,*/}
        {/*        style: {*/}
        {/*          fontFamily: "Poppins, sans-serif",*/}
        {/*        },*/}
        {/*      }}*/}
        {/*      InputLabelProps={{*/}
        {/*        style: {*/}
        {/*          fontFamily: "Poppins, sans-serif",*/}
        {/*        },*/}
        {/*      }}*/}
        {/*      sx={{*/}
        {/*        fontFamily: "Poppins",*/}
        {/*        width: isSmallScreen ? "100%" : 200,*/}
        {/*      }}*/}
        {/*    />*/}
        {/*    <Button*/}
        {/*      variant="contained"*/}
        {/*      color="primary"*/}
        {/*      onClick={handleSetTimer}*/}
        {/*      sx={{ fontFamily: "Poppins" }}*/}
        {/*    >*/}
        {/*      Set Timer*/}
        {/*    </Button>*/}
        {/*  </Box>*/}
        {/*)}*/}
      </Box>
    </Box>
  );
};

export default Configuration;
